import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useHistory } from 'react-router-dom';
import { UserProvider, UserContext } from '../src/context/UserContext';
import { Button } from '@/components/ui/button';
import Dashboard from './components/Dashboard/Dashboard';
import Login from './components/Login/Login';
import UserProfile from './components/UserProfile/UserProfile';
import SignUp from './components/Signup/SignUp';
import './styles.css';

const App = () => {
  return (
    <UserProvider>
      <Router>
        <div className="app">
          <Nav />
          <Switch>
            <Route path="/" exact component={Dashboard} />
            <Route path="/login" component={Login} />
            <Route path="/signup" component={SignUp} />
            <Route path="/profiles" component={UserProfile} />
          </Switch>
        </div>
      </Router>
    </UserProvider>
  );
};

const Nav = () => {
  const { user, logout } = useContext(UserContext);
  const history = useHistory();

  const handleLogout = () => {
    logout();
    history.push('/login');
  };

  return (
    <nav className="flex justify-between items-center bg-blue-600 p-4 shadow-md text-white">
      <div className="text-xl font-bold">Agile Track</div>
      <div className="flex gap-4">
        <Link to="/">
          <Button variant="ghost" className="text-white hover:bg-blue-700">Dashboard</Button>
        </Link>
        {user ? (
          <>
            <Link to="/profiles">
              <Button variant="ghost" className="text-white hover:bg-blue-700">Profiles</Button>
            </Link>
            <Button onClick={handleLogout} variant="destructive" className="text-white">Logout</Button>
          </>
        ) : (
          <Link to="/login">
            <Button variant="ghost" className="text-white hover:bg-blue-700">Login</Button>
          </Link>
        )}
      </div>
    </nav>
  );
};

export default App;
