import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useHistory } from 'react-router-dom';
import { UserProvider, UserContext } from '../src/context/UserContext';
import Dashboard from './components/Dashboard/Dashboard';
import Login from './components/Login/Login';
import UserProfile from './components/UserProfile/UserProfile';
import SignUp from './components/Signup/SignUp';
import './styles.css';

const App = () => {
  return (
    <UserProvider>
      <Router>
        <div className="app">
          <Nav />
          <Switch>
            <Route path="/" exact component={Dashboard} />
            <Route path="/login" component={Login} />
            <Route path="/signup" component={SignUp} />
            <Route path="/profiles" component={UserProfile} />
          </Switch>
        </div>
      </Router>
    </UserProvider>
  );
};

const Nav = () => {
  const { user, logout } = useContext(UserContext);
  const history = useHistory();

  const handleLogout = () => {
    logout();
    history.push('/login');
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-white/10 backdrop-blur-lg p-4 flex justify-between items-center shadow-lg rounded-b-xl">
      <div className="text-xl font-semibold text-white">Agile Track</div>
      <div className="flex gap-4">
        <Link to="/" className="text-white hover:underline">Dashboard</Link>
        {user ? (
          <>
            <Link to="/profiles" className="text-white hover:underline">Profiles</Link>
            <button onClick={handleLogout} className="text-white hover:underline">Logout</button>
          </>
        ) : (
          <Link to="/login" className="text-white hover:underline">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default App;
